ron = "Рон, нужно больше стараться!"
marks = input().split(', ')
m = int(input())

good = []
for i in marks:
    if int(i.split(' : ')[1]) >= m:
        good.append(i.split(' : ')[0])

if len(good) > 0:
    print(', '.join(sorted(good)))
else:
    print(ron)

airlines = {
    'Аэро_зум': {'12iy34': ['Милан', 'Париж', 'Казань'], '193tyw': ['Рим', 'Токио']},
    'Аэрофлот_экспресс': {'12oa': ['Дублин', 'Токио', 'Рим'], 'llwo24': ['Рим']},
